import "core-js/modules/es.array.push.js";
import StringUtil from '../../utils/StringUtil';
export default {
  name: 'App',
  data() {
    return {
      server_info: process.env.VUE_APP_MODE
    };
  },
  computed: {
    userInfo() {
      // 로그인 성공 여부 & 에러 메시지
      return this.$store.state.auth.userInfo;
    }
  },
  methods: {
    logout() {
      // localStorage 초기화
      localStorage.clear();
      // logout 호출
      this.$store.dispatch("auth/signout").then(() => {
        // 로그아웃 성공
        this.$router.push({
          path: '/login'
        });
      }).catch(error => {
        // 로그아웃 호출 실패. 로그인 페이지로 이동
        console.log(error);
        this.$router.push({
          path: '/login'
        });
      });
    },
    StringUtil() {
      var obj = StringUtil;
      return obj;
    }
  }
};
